mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail}, manufacture_day = #{manuDate}, price = #{price}, image_file = #{fileName}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT prod_name, price, reg_date, prod_no FROM product
prod_name = #{searchKeyword}
ORDER BY prod_name ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
